import './Games.css'
import React from 'react'
import PlayerCard from '../PlayerCard'


export const Games = (props) => {

    /* const fundoVar = {backgroundColor: props.corSecundaria};  */

    return (
        (props.players.length >0) && // ou ? e : '' no final (ternario)
        <section className='games-section' style={{backgroundColor: props.corSecundaria, backgroundImage: `url(${props.imgFundo})`, backgroundRepeat: 'no-repeat', backgroundClip: 'stretch'}}>
            <h3 style={{borderColor: props.corPrimaria}}>{props.nome}</h3>
            <div className='players'>
            {props.players.map(player => <PlayerCard 
            corDeFundo={props.corPrimaria}
            key={player.nome} nome={player.nome} position={player.position} tag={player.tag}
            imgFundo={player.imgFundo}
            />)}
            </div>
        </section>

    )

}
