import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap/dist/css/bootstrap.min.css';

import Banner from './components/Banner';
import Formulario from './components/Formulario';
import React, { useState }  from 'react';
import  Games  from './components/Games';

function App() {

  const [players, setPlayers] = useState([]);

  const playerAdicionado = (player) =>{
    console.log(player)
    setPlayers([...players, player])   // adicionando novo player dentro da array com useState
                                        // ...players eh a syntaxe para espalhar os players antigos na array
  }

  return (
    <div className="App">
      <Banner />
      <Formulario addPlayerNovo={ player => playerAdicionado(player)}/>
      <Games nome="OverWatch2"/>
      <Games nome="World of Warcraft"/>
    </div>
  );
}

export default App;
