import 'bootstrap/dist/css/bootstrap.css'
// Put any other imports below so that CSS from your
// components takes precedence over default styles.
import React, { useState } from 'react'
import CampoTexto from '../CampoTexto'
import './Formulario.css'
import DropDown from '../DropDown'
import BotaoForm from '../BotaoForm'

export const Formulario = (props) => {
/* 
    const games =[
        'OverWatch2',
        'World of Warcraft',
        'League of Legends',
        'Call of Duty MW2',
        'Dota 2',
        'Final Fantasy XIV',
        'CS: Global Offensive'
    ];
 */
    const [nome, setNome] = useState('');
    const [position, setPosition] = useState('');
    const [tag, setTag] = useState('');
    const [game, setGame] = useState('');

    const addPlayer = (evento) => {
        evento.preventDefault();
        props.addPlayerNovo({
            nome,
            position,
            tag,
            game
        })
       
    }

    return (
    <div className="container-fluid formulario">
        <form onSubmit={addPlayer}>   
            <h2>Cadastre seu Usertag e seja chamado no LFG!</h2>       
            <CampoTexto 
            obrigatorio={true} 
            label="Nome" 
            placeholder="Digite o seu nome"
            valor={nome}
            aoAlterar={valor => setNome(valor)}/>
            <CampoTexto 
            obrigatorio={true} 
            label="Posiçao" 
            placeholder="Digite a sua posição" 
            valor={position}
            aoAlterar={valor => setPosition(valor)}/>
            <CampoTexto 
            label="Usertag" 
            placeholder="Digite o endereço da sua Usertag" 
            valor={tag}
            aoAlterar={valor => setTag(valor)}/>
            <DropDown 
            obrigatorio={true} 
            label="Escolha seu Game" 
            itens={props.games}
           
            valor={game}
            aoAlterar={valor => setGame(valor)}

           />
            <BotaoForm>
                Adicionar o Player
            </BotaoForm>
        </form>
    </div>

    )

}
