import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import Formulario from './components/Formulario'
import GamesCards from './components/GamesCards'
import Banner from './components/Banner';
import React, {useState} from 'react';
import  Games  from './components/Games';

function App() {

  var players =[
    {
      nome: 'Pedro Queiróz',
      position: 'Coach',
      tag: 'https://github.com/Keirz.png',
      game: 'OverWatch2' 
    }
  ]

  const games = [
   {
    nome: 'OverWatch2',
    corPrimaria: '#57c278',
    corSecundaria: '#d9f7e9',
    imgFundo: './public/images/overwatch2.jpg' 
   },  
    {
    nome: 'World of Warcraft',
    corPrimaria: '#82cffa',
    corSecundaria: '#e8f8ff'
   }, 
    {
    nome: 'League of Legends',
    corPrimaria: '#A6D157',
    corSecundaria: '#f0f8e2'
   }, 
    {
    nome: 'Call of Duty MW2',
    corPrimaria: '#e06b69',
    corSecundaria: '#fde7e8'
   },  
   {
    nome: 'Dota 2',
    corPrimaria: '#db6ebf',
    corSecundaria: '#fae6f5'
   },  
   {
    nome: 'CS: Global Offensive',
    corPrimaria: '#ff8a29',
    corSecundaria: '#ffeedf'
   }
  ]


  const [player, setPlayers] = useState([]);

  const playerAdicionado = (player) =>{
    /* console.log(player) */
    debugger
    setPlayers([...players, player])   // adicionando novo player dentro da array com useState
                                        // ...players eh a syntaxe para espalhar os players antigos na array
  }

  return (
    <div className="App">
      <Banner />
      <>
      <Formulario games={games.map(game => game.nome)} 
            addPlayerNovo={ player => playerAdicionado(player)}/>

      {games.map(game =><GamesCards 
      key={game.nome}
      nome={game.nome}
      imgFundo={game.imgFundo}
      corPrimaria={game.corPrimaria}
      corSecundaria={game.corSecundaria}/* 
      qtd={players.filter(player => player.game === game.nome).length} */
      />)}
      </>

      {games.map(game => <Games 
      key={game.nome} 
      nome={game.nome} 
      corPrimaria={game.corPrimaria} 
      corSecundaria={game.corSecundaria}
      players={players.filter(player => player.game === game.nome)} /> )}

    </div>
  );
}

export default App;
