import './Games.css'
import React from 'react'
import PlayerCard from '../PlayerCard'


export const Games = (props) => {

    const fundoVar = {backgroundColor: props.corSecundaria}; 

    return (
        (props.players.lenght >0) && // ou ? e : '' no final (ternario)
        <section className='games-section' style={{backgroundColor: props.corSecundaria}}>
            <h3 style={{borderColor: props.corPrimaria}}>{props.nome}</h3>
            <div className='players'>
            {props.players.map(player => <PlayerCard nome={player.nome} position={player.position} tag={player.tag}/>)}
            </div>
        </section>

    )

}
