import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap/dist/css/bootstrap.min.css';

import Banner from './components/Banner';
import Formulario from './components/Formulario';
import React, { useState }  from 'react';
import  Games  from './components/Games';

function App() {

  const games = [
   {
    nome: 'OverWatch2',
    corPrimaria: '#d9f7e9',
    corSecundaria: '#57c278'
   },  
    {
    nome: 'World of Warcraft',
    corPrimaria: '',
    corSecundaria: ''
   }, 
    {
    nome: 'League of Legends',
    corPrimaria: '',
    corSecundaria: ''
   }, 
    {
    nome: 'Call of Duty MW2',
    corPrimaria: '',
    corSecundaria: ''
   },  
   {
    nome: 'Dota 2',
    corPrimaria: '',
    corSecundaria: ''
   }, 
    {
    nome: 'Final Fantasy XIV',
    corPrimaria: '',
    corSecundaria: ''
   },  
   {
    nome: 'CS: Global Offensive',
    corPrimaria: '',
    corSecundaria: ''
   }
  ]

  const [players, setPlayers] = useState([]);

  const playerAdicionado = (player) =>{
    console.log(player)
    setPlayers([...players, player])   // adicionando novo player dentro da array com useState
                                        // ...players eh a syntaxe para espalhar os players antigos na array
  }

  return (
    <div className="App">
      <Banner />
      <Formulario addPlayerNovo={ player => playerAdicionado(player)}/>
      <Games nome="OverWatch2"/>
      <Games nome="World of Warcraft"/>
      <Games nome="League of Legends"/>
      <Games nome="Call of Duty MW2"/>
      <Games nome="Dota 2"/>
      <Games nome="Final Fantasy XIV"/>
      <Games nome="CS: Global Offensive"/>
    </div>
  );
}

export default App;
