import React from 'react'
import myData from './data.json'
import './List.css'

export const List = (props) => {
    //create a new array by filtering the original array
    const filteredData = myData.filter((el) => {
        //if no input the return the original
        if (props.input === '') {
            return el;
        }
        //return the item which contains the user input
        if(props.input.toLowerCase() == el.nome.toLowerCase()) {
            return el.nome.toLowerCase().includes(props.input)
        }
        if(props.input.toLowerCase() == el.game.toLowerCase()){
            return el.game.toLowerCase().includes(props.input)
        }
    })
    
    return (
        <ul className='listaplayers'>
            {filteredData.map((item) => (
                <li className='linha'>Player:{item.nome}, <br/> Jogo:{item.game},<br/> Posição: {item.position},<br/> Discord Tag: {item.Disctag}</li>
                
            ))}
        </ul>
    )
}
