import './PlayerCard.css'
import React from 'react';


export const PlayerCard = ({nome, tag, position, imgFundo}) =>{

    return(
        <div className='player-card'>
            <div className='cabecalho' style={{backgroundImage: `url(${imgFundo})`, backgroundRepeat: 'no-repeat', backgroundSize: '100%'}}>
                <img src={tag} alt={nome} />
            </div>
            <div className='rodape'>
                <h4>{nome}</h4>
                <h5>{tag}</h5>
                <h5>{position}</h5>
            </div>
        </div>

    )
}
