            id,
            nome,
            position,
            Disctag,
            game,
            tag
        })
       setNome('');
       setGame('');
       setPosition('');
       setDisctag('');
      

      
       
       
    }

    return (
    <div className="container-fluid formulario">
        <Form onSubmit={(addPlayer)} key='formulario'>   
            <h2 key='h2form'>Cadastre seu Usertag e seja chamado no LFG!</h2>       
            <CampoTexto 
            key='inputNome'
            obrigatorio={true} 
            label="Nome" 
            placeholder="Digite o seu nome"
            valor={nome}
            aoAlterar={valor => setNome(valor)}/>
            <CampoTexto 
            key='inputPos'
            obrigatorio={true} 
            label="Posiçao" 
            placeholder="Digite a sua posição" 
            valor={position}
            aoAlterar={valor => setPosition(valor)}/>
            <CampoTexto 
            key='inputUtag'
            label="Usertag" 
            placeholder="Digite a Usertag do seu Discord!" 
            valor={Disctag}
            aoAlterar={valor => setDisctag(valor)}/>
            <DropDown 
            key='dropdown'
            obrigatorio={true} 
            label="Escolha seu Game" 
            itens={props.games}
            placeholder="Escolha o game"
            valor={game}
            aoAlterar={valor => setGame(valor)}

           />
            <BotaoForm  key='botaoform'>
                Adicionar o Player
            </BotaoForm>
        </Form>
    </div>

    )

}
